  void render_frame(void) {
    switch (current_state) {
      case STATE_PREP:
        for (int i = 0; i < 8; i++) {
          oled_set_cursor(0, i);
          oled_write_raw(prep[0][i], ANIM_SIZE / 8);
        }
        break;
      case STATE_GALLOP:
        for (int i = 0; i < 8; i++) {
          oled_set_cursor(0, i);
          oled_write_raw(gallop[current_gallop_frame][i], ANIM_SIZE / 8);
        }
        break;
      default:
        for (int i = 0; i < 8; i++) {
          oled_set_cursor(0, i);
          oled_write_raw(idle[current_idle_frame][i], ANIM_SIZE / 8);
        }
        break;
    }
  }

  void animation_phase(void) {
    if (get_current_wpm() <= IDLE_SPEED) {
      current_idle_frame = (current_idle_frame + 1) % IDLE_FRAMES;
      current_state = STATE_IDLE;
    }

    if (get_current_wpm() > IDLE_SPEED && get_current_wpm() < ANIM_WPM_LOWER) {
      current_state = STATE_PREP;
    }

    if (get_current_wpm() >= ANIM_WPM_LOWER) {
      current_gallop_frame = (current_gallop_frame + 1) % GALLOP_FRAMES;
      current_state = STATE_GALLOP;
    }
    
    render_frame();
  }

  // variable animation duration. Don't want this value to get near zero as it'll bug out.
  curr_anim_duration = MAX(ANIM_FRAME_DURATION_MIN, ANIM_FRAME_DURATION_MAX - ANIM_FRAME_RATIO * get_current_wpm());

  if (get_current_wpm() > ANIM_WPM_LOWER) {
    oled_on();

    if (timer_elapsed32(idle_timer) > curr_anim_duration) {
      idle_timer = timer_read32();
      animation_phase();
    }
  } else {
    if (timer_elapsed32(idle_timer) > IDLE_FRAME_DURATION) {
      idle_timer = timer_read32();
      animation_phase();
    }
  }
